import {useFieldArray,useForm} from "react-hook-form"
export const DynamicFields=()=>{
    const{register,control,formState:{errors,isDirty},handleSubmit,watch}=useForm({
        defaultValues:{education:[{ }]}
    })
    const{fields,append,remove}=useFieldArray({control,name:"education"})

    const submitForm=(data)=>{
        console.log(data)
    }
    return(
        <>
        <div className="h-screen w-screen before:content-[''] before:w-full before:h-full before:bg-black before:absolute before:-z-10 isolate before:inset-0 before:opacity-30 absolute top-0 left-0 flex justify-center items-center">
            <div className="bg-white w-1/3">

            <form action="" className="w-full bg-red-400" onSubmit={handleSubmit(submitForm)}>
                {
                    fields.map((item,i)=>{
                        return <div className=" flex w-full" key={item.id}>
                            <div className="flex flex-col w-full">
                            <div className="w-full flex">
                            <select name="" id=""{...register(`education.${i}.course`)} className="w-full">
                                <option value="">Select..</option>
                                <option value="Graduation">Graduation</option>
                                <option value="Post-Graduation">Post-Graduation</option>
                                <option value="Intermediate">Intermediate</option>
                                <option value="10TH">10TH</option>
                            </select>
                            {
                                watch(`education.${i}.course`)==="Graduation" && <select className="w-full" name="" id=""{...register(`education.${i}.degree`)}>
                                <option value="">Select..</option>
                                <option value="B.Tech">B.Tech</option>
                                <option value="B.Com">B.Com</option>
                                <option value="B.Sc">B.Sc</option>
                            </select>
                            }
                            </div>

                            <input type="text" className="px-5 py-2 border-[1px] border-black" {...register(`education.${i}.institute`)} />
                            <input type="text" {...register(`education.${i}.cgpa`)} />
                            </div>

                                 <button type="button"><i class="fa-solid fa-trash"></i></button>

                        </div>
                    })
                }
            </form>
            </div>
        </div>
        </>
    )
}
