import L from "leaflet";
import {MapContainer,Marker,TileLayer,Popup,useMapEvents} from "react-leaflet"
import MarkIcon from "leaflet/dist/images/marker-icon.png"
import "leaflet-routing-machine"
import { useEffect, useRef, useState } from "react";
import "leaflet/dist/leaflet.css";
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl:MarkIcon
})
const App=()=>{
const[currentPosition,setCurrent]=useState([]);
const[destination,setDestination]=useState([])
const mapRef=useRef();

  useEffect(()=>{
    navigator.geolocation.watchPosition((postition)=>{
      const{latitude,longitude}=postition.coords;
      setCurrent([latitude,longitude])
    },(err)=>{
      console.log("error ....")
    },{enableHighAccuracy:true})
  },[])

  useEffect(()=>{
    if(currentPosition.length==2 && destination.length==2){
      const map=mapRef.current
      L.Routing.control({
        waypoints:[
          L.latLng(currentPosition[0],currentPosition[1]),
          L.latLng(destination[0],destination[1])
        ],routeWhileDragging:true
      }).addTo(map)
    }
  },[currentPosition,destination])

  const SetDestination=()=>{
    useMapEvents({
      click:(e)=>{
        const{lat,lng}=e.latlng
        setDestination([lat,lng])
      }
    })
    return null;
  }

  return (

    <>
    <MapContainer center={currentPosition } zoom={13} style={{height:"100vh",width:"100%"}} ref={mapRef}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'></TileLayer>
      {
        currentPosition.length==2 && <Marker position={currentPosition} >
        <Popup>hello jstar</Popup>
      </Marker>
      }
      {
        destination.length==2 && <Marker position={destination} >
        <Popup>hello jstar</Popup>
      </Marker>
      }
      <SetDestination/>
    </MapContainer>
    </>
  )
}

export default App;
