import {useFieldArray,useForm, useWatch} from "react-hook-form"

export const DynamicFields=()=>{
    const{control,register,handleSubmit,watch}=useForm({defaultValues:{users:[{name:""}]}})
    const{fields,append,remove}=useFieldArray({
        control,
        name:"users"
    })

    const submitForm=(data)=>{
        console.log(data.users[0].name)
    }

    return(
        <>
       <form action="" onSubmit={handleSubmit(submitForm)} className="w-1/3">
        <p>Enter Education Details</p>
        <div>
        {
            fields.map((ii,i)=>{
                return <div key={ii.id} className="flex flex-col">
                    <div>
                    <select name="" id="" {...register(`users.${i}.courseName`)}>
                        <option value="">Select....</option>
                        <option value="UG">Graduation</option>
                        <option value="PG">Post-Graduation</option>
                        <option value="Inter">Intermediate</option>
                        <option value="10">10TH</option>
                    </select>
                    {
                        watch(`users.${i}.courseName`)==="UG" && <select name="" id="" {...register(`users.${i}.gradutionName`)}>
                            <option value="">Select....</option>
                            <option value="B.Tech">B.Tech</option>
                            <option value="B.Com">B.Com</option>
                        </select>
                    }
                    </div>
                    <input className="border-2" placeholder="Enter Instituename"  type="text" {...register(`users.${i}.institutename`)}/>
                    <input className="border-2" placeholder="Enter CGPA" type="text" {...register(`users.${i}.CGPA`)}/>
                    <button onClick={()=>remove(i)}>remove</button>
                </div>
            })

        }
             <div className="flex gap-3">
             <button type="button" className="border-2 " onClick={()=>append({name:""})}>Add</button>
             <button type="submit">Submit</button>
             </div>
        </div>
       </form>
        </>
    )
}
