import { useForm } from "react-hook-form";
import axios from "axios";

const Login = () => {
    const { register, handleSubmit, formState: { errors } } = useForm();

    const SubmitForm = async (data) => {
        try {
            // Make POST request to login API
            const response = await axios.post("http://localhost:4000/api/auth/login", data);

            if (response.status === 200) {
                console.log("Login successful", response.data);
                // You can save the token to local storage or state
                // localStorage.setItem("token", response.data.token);
            }
        } catch (err) {
            if (err.response) {
                // This block handles the error from the backend (401 or 500)
                console.log("Error response:", err.response.data);
                // Display the message received from the backend
                alert(err.response.data.message || "An error occurred during login");
            } else {
                // This block handles network errors or Axios errors
                console.log("Error message:", err.message);
                alert("An error occurred during login");
            }
        }
    };

    return (
        <>
            <div className="h-screen w-screen flex justify-center items-center">
                <div className="w-[25%] border-2 py-10 px-5">
                    <h5 className="text-center mb-3">User Login</h5>
                    <form className="w-full flex flex-col space-y-3" onSubmit={handleSubmit(SubmitForm)}>
                        <input
                            className={`border-2 px-3 py-2 focus:outline-none ${errors.username && 'border-red-400'}`}
                            placeholder="Enter Username"
                            type="text"
                            {...register("username", { required: { message: "Enter Username", value: true } })}
                        />
                        <input
                            className={`border-2 px-3 py-2 focus:outline-none ${errors.password && 'border-red-400'}`}
                            placeholder="Enter Password"
                            type="password"
                            {...register("password", { required: { message: "Enter Password", value: true } })}
                        />
                        <input className="border-2 bg-green-200 py-2 font-bold text-xl" type="submit" value="Login" />
                    </form>
                </div>
            </div>
        </>
    );
};

export default Login;
