import React from 'react';
import { useForm, useFieldArray } from "react-hook-form";

const App = () => {
  const { control, handleSubmit } = useForm({
    defaultValues: {
      items: [
        { name: "", subItems: [{ subName: "" }] }, // Nested structure
      ],
    },
  });

  const { fields: mainFields, append: appendMain } = useFieldArray({
    control,
    name: "items",
  });

  const onSubmit = (data) => console.log(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      {mainFields.map((field, index) => (
        <div key={field.id}>
          <input
            {...{
              ...field,
              name: `items[${index}].name`,
              ref: undefined,
            }}
            placeholder="Enter item name"
          />

          <useFieldArray
            control={control}
            name={`items[${index}].subItems`}
            render={({ fields: subFields, append: appendSub }) => (
              <>
                {subFields.map((subField, subIndex) => (
                  <div key={subField.id}>
                    <input
                      {...{
                        ...subField,
                        name: `items[${index}].subItems[${subIndex}].subName`,
                        ref: undefined,
                      }}
                      placeholder="Enter sub-item name"
                    />
                  </div>
                ))}
                <button type="button" onClick={() => appendSub({ subName: "" })}>
                  Add Sub-Item
                </button>
              </>
            )}
          />
        </div>
      ))}
      <button type="button" onClick={() => appendMain({ name: "", subItems: [{ subName: "" }] })}>
        Add Item
      </button>
      <button type="submit">Submit</button>
    </form>
  );
};

export default App;
