import L from "leaflet";
import { MapContainer, Marker, TileLayer, Popup, useMapEvents } from "react-leaflet";
import MarkIcon from "leaflet/dist/images/marker-icon.png";
import "leaflet-routing-machine";
import { useEffect, useState, useRef } from "react";
import "leaflet/dist/leaflet.css";

// Remove the default icon handling to avoid issues with missing icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: MarkIcon,
});

const App = () => {
  const [currentPosition, setCurrent] = useState([51.505, -0.09]);
  const [destination, setDestination] = useState([]);
  const [destinationMarker, setDestinationMarker] = useState(null); // State for destination marker

  // Watch geolocation updates
  useEffect(() => {
    navigator.geolocation.watchPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setCurrent([latitude, longitude]);
      },
      (err) => {
        console.log("error ....");
      },
      { enableHighAccuracy: true }
    );
  }, []); // Only run once on mount

  // Effect to handle routing when destination or current position changes
  useEffect(() => {
    if (currentPosition && destination.length === 2) {
      // You can remove the old destination marker here before adding a new one
      if (destinationMarker) {
        destinationMarker.remove(); // Remove previous marker
      }
      const newMarker = L.marker(destination).addTo(mapRef.current); // Add new destination marker
      setDestinationMarker(newMarker); // Store the new marker in the state

      // Add the routing control
      L.Routing.control({
        waypoints: [
          L.latLng(currentPosition[0], currentPosition[1]),
          L.latLng(destination[0], destination[1]),
        ],
      }).addTo(mapRef.current);
    }
  }, [currentPosition, destination]);

  // Function to handle click and set destination
  const SetDestination = () => {
    useMapEvents({
      click: (e) => {
        const { lat, lng } = e.latlng;
        setDestination([lat, lng]); // Update destination
      },
    });
    return null;
  };

  const mapRef = useRef(); // Create a reference for the map

  return (
    <>
      <MapContainer
        center={currentPosition}
        zoom={13}
        style={{ height: "100vh", width: "100%" }}
        ref={mapRef}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
        />
        {currentPosition.length === 2 && (
          <Marker position={currentPosition}>
            <Popup>Current Location</Popup>
          </Marker>
        )}
        {destination.length === 2 && (
          <Marker position={destination}>
            <Popup>Destination</Popup>
          </Marker>
        )}
        <SetDestination />
      </MapContainer>
    </>
  );
};

export default App;
