import L from "leaflet";
import {MapContainer,Marker,TileLayer,Popup,useMapEvents} from "react-leaflet"
import MarkIcon from "leaflet/dist/images/marker-icon.png"
import "react-leaflet-routing-machine";
import { useEffect, useState } from "react";
import "leaflet/dist/leaflet.css";
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl:MarkIcon
})
const App=()=>{
const[currentPosition,setCurrent]=useState([51.505, -0.09]);
const[destination,setDestination]=useState([])

  useEffect(()=>{
    navigator.geolocation.watchPosition((postition)=>{
      const{latitude,longitude}=postition.coords;
      setCurrent([latitude,longitude])
    },(err)=>{
      console.log("error ....")
    },{enableHighAccuracy:true})
  },[currentPosition])

  useEffect(()=>{
    if(currentPosition && destination){
      L.Routing.control({
        waypoints: [
            L.latLng(currentPosition[0], currentPosition[1]),
            L.latLng(destination[0], destination[1]),
        ],
        routeWhileDragging: true,
    }).addTo(map);
    }
  },[currentPosition,destination])

  const SetDestination=()=>{
    useMapEvents({
      click:(e)=>{
        const{lat,lng}=e.latlng
        setDestination([lat,lng])
      }
    })
    return null;
  }

  return (

    <>
    <MapContainer center={currentPosition } zoom={13} style={{height:"100vh",width:"100%"}} >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'></TileLayer>
      <Marker position={currentPosition} >
        <Popup>hello jstar</Popup>
      </Marker>
    </MapContainer>
    </>
  )
}

export default App;
