import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { userData } from "./userData.js";
import Popup from "./Popup.jsx";
import axios from "axios";

const UserData=()=>{
    const[data,setData]=useState([]);
    const[Currdata,setCurrData]=useState(null);

    const[pop,setPop]=useState(false)

    const handlePop=(currentData)=>{
        setCurrData(currentData)
        setPop((p)=>!p)
    }
    const updateData=(data)=>{
        setData((prev)=>prev.map((ii)=>ii.id===data.id ? data : ii))
    }
    useEffect(()=>{
        axios.get("http://localhost:4000/allEmp").then((res)=>{
            setData(res.data)
        })
    })
    console.log(data)
    return(
        <>
        <div>
            <table className=" border-2 w-1/2">
                <tr className="h-10 bg-gray-300 text-center">
                    <th>Id</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Location</th>
                    <th>Gender</th>
                    <th>Actions</th>
                </tr>
                {
                    Array.isArray(data)  && data.length!=0 && data.map((ii,i)=>{
                        return <tr key={i} className="text-center">
                            <td>{ii.id}</td>
                            <td>{ii.name}</td>
                            <td>{ii.email}</td>
                            <td>{ii.phone}</td>
                            <td>{ii.city}</td>
                            <td>{ii.gender}</td>
                            <td><button className="bg-green-200 px-5 py-2" onClick={()=>handlePop(ii)}>Edit</button></td>
                        </tr>
                    })
                }
            </table>
        </div>
        {
            pop && <Popup open={setPop} data={Currdata} update={updateData}/>
        }
        </>
    )
}


export default UserData;
