import {useForm,useFieldArray} from "react-hook-form"
 const App=()=>{
  const {register,handleSubmit,unregister,formState:{errors,isSubmitted},getValues,watch,control} =useForm();
  const submitForm=(data)=>{
    console.log(data)
    console.log(isSubmitted)
  }
  const{fields,append,remove}=useFieldArray({control:control,name:"name"})
  return(
    <>
    <form action="" onSubmit={handleSubmit(submitForm)} className="flex flex-col w-72">
      {
        fields.map((field,index)=>(
          <div key={field.id}>
            <input type="text" {...register(`name.${index}.fname`)} />
            <input type="text" {...register(`name.${index}.lname`)} />
            <button onClick={()=>remove(index)}>remove</button>
          </div>
        ))
      }
      <button onClick={()=>append({name:""})}>Add</button>
      <input type="submit" value="submit" />
    </form>
    </>
  )
}


export default App
