import L from "leaflet";
import { MapContainer, Marker, TileLayer, Popup, useMapEvents } from "react-leaflet";
import MarkIcon from "leaflet/dist/images/marker-icon.png";
import "react-leaflet-routing-machine";
import { useEffect, useState } from "react";
import "leaflet/dist/leaflet.css";

// Fix marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: MarkIcon
});

const App = () => {
  const [currentPosition, setCurrent] = useState([51.505, -0.09]);
  const [destination, setDestination] = useState(null); // Start with null for destination

  useEffect(() => {
    navigator.geolocation.watchPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setCurrent([latitude, longitude]);
      },
      (err) => {
        console.log("Error: ", err);
      },
      { enableHighAccuracy: true }
    );
  }, []);

  // Handle destination setting on map click
  const SetDestination = () => {
    useMapEvents({
      click: (e) => {
        const { lat, lng } = e.latlng;
        setDestination([lat, lng]);
      }
    });
    return null;
  };

  return (
    <>
      <MapContainer
        center={currentPosition}
        zoom={13}
        style={{ height: "100vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        {
          // Ensure that currentPosition is a valid array before rendering the marker
          Array.isArray(currentPosition) && currentPosition.length === 2 && (
            <Marker position={currentPosition}>
              <Popup>Current Location</Popup>
            </Marker>
          )
        }
        {
          // Ensure that destination is a valid array before rendering the marker
          destination && Array.isArray(destination) && destination.length === 2 && (
            <Marker position={destination}>
              <Popup>Destination</Popup>
            </Marker>
          )
        }
        <SetDestination />
      </MapContainer>
    </>
  );
};

export default App;
